import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [context, setContext] = useState('');
  const [responseType, setResponseType] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    setLoading(true);
    setResponseType(null);
    try {
      const response = await axios.post('http://localhost:8000/predict', {
        context,
      });
      setResponseType(response.data.response_type);
    } catch (error) {
      console.error("Prediction failed", error);
      setResponseType("Error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '40px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h1>Response Type Predictor</h1>
      <textarea
        rows="5"
        value={context}
        onChange={(e) => setContext(e.target.value)}
        placeholder="Enter the patient's context here..."
        style={{ width: '100%', padding: '10px', marginTop: '10px' }}
      />
      <button
        onClick={handleSubmit}
        disabled={loading}
        style={{ marginTop: '10px', padding: '10px 20px' }}
      >
        {loading ? "Predicting..." : "Get Response Type"}
      </button>
      {responseType && (
        <div style={{ marginTop: '20px', fontWeight: 'bold' }}>
          Suggested Response Type: <span style={{ color: 'blue' }}>{responseType}</span>
        </div>
      )}
    </div>
  );
}

export default App;
