import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [context, setContext] = useState('');
  const [responseType, setResponseType] = useState(null);
  const [generatedResponse, setGeneratedResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [generating, setGenerating] = useState(false);

  const handleSubmit = async () => {
    setLoading(true);
    setResponseType(null);
    setGeneratedResponse(null);
    try {
      const response = await axios.post('http://localhost:8000/predict', { context });
      setResponseType(response.data.response_type);
    } catch (error) {
      console.error("Prediction failed", error);
      setResponseType("Error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleGenerate = async () => {
    setGenerating(true);
    setGeneratedResponse(null);
    try {
      const response = await axios.post('http://localhost:8000/generate-response', {
        context,
        response_type: responseType
      });
      setGeneratedResponse(response.data.generated_response);
    } catch (error) {
      console.error("Generation failed", error);
      setGeneratedResponse("Failed to generate response.");
    } finally {
      setGenerating(false);
    }
  };

  return (
    <div style={{
      maxWidth: '700px',
      margin: '40px auto',
      padding: '30px',
      backgroundColor: '#f9fafb',
      borderRadius: '12px',
      boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)'
    }}>
      <h1 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '20px' }}>
        🧠 Patient Counseling Assistant
      </h1>

      <textarea
        rows="6"
        value={context}
        onChange={(e) => setContext(e.target.value)}
        placeholder="📝 Enter the patient's context here..."
        style={{
          width: '100%',
          padding: '12px',
          borderRadius: '8px',
          border: '1px solid #ccc',
          fontSize: '16px',
          resize: 'vertical'
        }}
      />

      <button
        onClick={handleSubmit}
        disabled={loading}
        style={{
          marginTop: '15px',
          padding: '12px 24px',
          backgroundColor: '#2563eb',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
          fontWeight: 'bold'
        }}
      >
        {loading ? "🔄 Predicting..." : "🎯 Get Response Type"}
      </button>

      {responseType && (
        <div style={{ marginTop: '25px', fontWeight: 'bold', fontSize: '18px' }}>
          ✅ Suggested Response Type: <span style={{ color: '#2563eb' }}>{responseType}</span>
        </div>
      )}

      {responseType && (
        <button
          onClick={handleGenerate}
          disabled={generating}
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            backgroundColor: '#10b981',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontWeight: 'bold'
          }}
        >
          {generating ? "🧠 Generating..." : `💡 Generate Suggested ${responseType}`}
        </button>
      )}

      {generatedResponse && (
        <div style={{
          marginTop: '30px',
          padding: '15px',
          backgroundColor: '#f1f5f9',
          borderRadius: '8px',
          fontSize: '16px',
          lineHeight: '1.5'
        }}>
          🗨️ <strong>Suggested {responseType}:</strong><br />
          {generatedResponse}
        </div>
      )}
    </div>
  );
}

export default App;
