import React, { useState } from 'react';
import { AlertTriangle, Loader2, MessageSquare, Send } from 'lucide-react';

function App() {
  const [context, setContext] = useState('');
  const [responseType, setResponseType] = useState(null);
  const [suggestedResponse, setSuggestedResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const [generatingResponse, setGeneratingResponse] = useState(false);

  const handleSubmit = async () => {
    if (!context.trim()) return;

    setLoading(true);
    setResponseType(null);
    setSuggestedResponse('');

    try {
      const response = await fetch('http://localhost:8000/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ context }),
      });

      const data = await response.json();
      setResponseType(data.response_type);
    } catch (error) {
      console.error("Prediction failed", error);
      setResponseType("Error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const generateResponse = async () => {
    if (!responseType || responseType.includes("Error")) return;

    setGeneratingResponse(true);

    try {
      // This would be your actual OpenAI API call
      // For now, simulating a response based on the type and context
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer YOUR_OPENAI_API_KEY', // Replace with actual API key
        },
        body: JSON.stringify({
          model: "gpt-4",
          messages: [
            {
              role: "system", 
              content: `You are an expert counselor. Generate a ${responseType} response to the following patient context. Be empathetic, professional, and helpful.`
            },
            {
              role: "user",
              content: context
            }
          ],
          max_tokens: 300
        }),
      });

      const data = await response.json();
      setSuggestedResponse(data.choices[0].message.content);

      // Simulated response for demo purposes - remove this in production
      setTimeout(() => {
        const sampleResponses = {
          "advice": `Based on what you've shared, I recommend establishing a consistent routine that includes adequate sleep, balanced nutrition, and regular physical activity. Consider setting small, achievable goals and celebrating your progress. It might also be beneficial to connect with a support group who understands your experiences.`,
          "reflection": `It sounds like you're feeling overwhelmed by conflicting priorities and experiencing difficulty finding balance. The frustration you're expressing about your situation is understandable, and it seems like you're putting a lot of pressure on yourself to meet everyone's expectations.`,
          "validation": `What you're experiencing is completely valid. It takes courage to acknowledge these feelings and reach out for support. Many people face similar challenges, and your response to this situation is natural given the circumstances.`,
          "question": `How have you been coping with these feelings so far? What strategies have you tried in the past that have helped, even if only a little bit? Can you identify any specific triggers that make these feelings more intense?`,
          "empathy": `I can hear how difficult this situation has been for you. Facing these kinds of challenges day after day can be exhausting both emotionally and physically. It makes sense that you're feeling this way given everything you've been managing.`
        };

        setSuggestedResponse(sampleResponses[responseType.toLowerCase()] || "I understand your situation and I'm here to support you. Let's work together to address these concerns and develop strategies that work for you.");
        setGeneratingResponse(false);
      }, 1500);

    } catch (error) {
      console.error("Response generation failed", error);
      setSuggestedResponse("Error generating response. Please try again.");
    } finally {
      // Comment this line when using the actual API
      // setGeneratingResponse(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-2xl mx-auto bg-white rounded-xl shadow-md overflow-hidden">
        <div className="bg-blue-600 p-5 text-white">
          <h1 className="text-2xl font-bold">Counselor Response Assistant</h1>
          <p className="text-blue-100 mt-1">Helping you provide better support to your patients</p>
        </div>

        <div className="p-6">
          <div className="mb-6">
            <label className="block text-gray-700 font-medium mb-2">
              Patient Context
            </label>
            <textarea
              rows="6"
              value={context}
              onChange={(e) => setContext(e.target.value)}
              placeholder="Enter the patient's context here..."
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            />
          </div>

          <button
            onClick={handleSubmit}
            disabled={loading || !context.trim()}
            className={`flex items-center justify-center w-full py-3 px-4 rounded-lg font-medium ${
              loading || !context.trim() 
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                : 'bg-blue-600 text-white hover:bg-blue-700'
            }`}
          >
            {loading ? (
              <>
                <Loader2 className="animate-spin mr-2 h-5 w-5" />
                Analyzing Context...
              </>
            ) : (
              <>
                <MessageSquare className="mr-2 h-5 w-5" />
                Determine Response Type
              </>
            )}
          </button>

          {responseType && (
            <div className="mt-8 border-t pt-6">
              <div className="mb-4">
                <h2 className="text-lg font-semibold text-gray-800">Recommended Response Type:</h2>
                <div className="mt-2 inline-block bg-blue-100 text-blue-800 px-4 py-2 rounded-lg font-medium">
                  {responseType}
                </div>
              </div>

              <button
                onClick={generateResponse}
                disabled={generatingResponse || responseType.includes("Error")}
                className={`flex items-center justify-center w-full py-3 px-4 rounded-lg font-medium ${
                  generatingResponse || responseType.includes("Error")
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                    : 'bg-green-600 text-white hover:bg-green-700'
                }`}
              >
                {generatingResponse ? (
                  <>
                    <Loader2 className="animate-spin mr-2 h-5 w-5" />
                    Generating {responseType} Response...
                  </>
                ) : (
                  <>
                    <Send className="mr-2 h-5 w-5" />
                    Generate Suggested {responseType} Response
                  </>
                )}
              </button>
            </div>
          )}

          {suggestedResponse && (
            <div className="mt-6 bg-gray-50 p-5 rounded-lg border border-gray-200">
              <h3 className="text-lg font-semibold text-gray-800 mb-3">Suggested Response:</h3>
              <p className="text-gray-700 whitespace-pre-line">{suggestedResponse}</p>
            </div>
          )}
        </div>

        <div className="bg-blue-50 p-4 border-t border-blue-100 flex items-start">
          <AlertTriangle className="h-5 w-5 text-yellow-500 mt-0.5 mr-2 flex-shrink-0" />
          <p className="text-sm text-gray-600">
            This tool is meant to assist counselors and healthcare professionals, not replace professional judgment. Always review and adjust suggestions as needed.
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
