import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [context, setContext] = useState('');
  const [responseType, setResponseType] = useState(null);
  const [distribution, setDistribution] = useState({});
  const [loading, setLoading] = useState(false);
  const [suggestedResponse, setSuggestedResponse] = useState('');
  const [generating, setGenerating] = useState(false);
  const [selectedType, setSelectedType] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    setLoading(true);
    setSuggestedResponse('');
    setError('');

    try {
      const response = await axios.post('http://localhost:8000/predict', { context });
      setResponseType(response.data.response_type);
      setDistribution(response.data.distribution);
      setError('');
    } catch (error) {
      console.error("Prediction failed", error);
      setError("Failed to analyze the context. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleGenerate = async (type) => {
    setGenerating(true);
    setSelectedType(type);
    setError('');

    try {
      const res = await axios.post('http://localhost:8000/generate', {
        context,
        response_type: type
      });
      setSuggestedResponse(res.data.generated_response);
    } catch (err) {
      console.error("Generation failed", err);
      setError("Failed to generate response. Please try again.");
    } finally {
      setGenerating(false);
    }
  };

  const getColorForPercentage = (percentage) => {
    // Convert percentage to a value between 0 and 1
    const value = percentage / 100;

    // Color gradient from blue (low) to green (high)
    if (value > 0.7) return '#34D399'; // Green for high values
    if (value > 0.4) return '#60A5FA'; // Blue for medium values
    return '#9CA3AF'; // Gray for low values
  };

  return (
    <div className="bg-gray-50 min-h-screen py-10 px-4">
      <div className="max-w-3xl mx-auto bg-white rounded-xl shadow-md overflow-hidden">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-600 to-indigo-700 p-6">
          <h1 className="text-3xl font-bold text-white">Counselor Support Assistant</h1>
          <p className="text-blue-100 mt-2">AI-powered guidance for mental health professionals</p>
        </div>

        {/* Main content */}
        <div className="p-8">
          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="context">
              Patient Context
            </label>
            <textarea
              id="context"
              rows="6"
              value={context}
              onChange={(e) => setContext(e.target.value)}
              placeholder="Enter the patient's context or conversation excerpt here..."
              className="shadow appearance-none border rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>

          <div className="flex justify-center">
            <button
              onClick={handleSubmit}
              disabled={loading || !context}
              className={`flex items-center justify-center px-6 py-3 rounded-lg text-white font-medium ${!context || loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'}`}
            >
              {loading ? (
                <>
                  <span className="animate-spin mr-2">⟳</span>
                  Analyzing...
                </>
              ) : (
                "Analyze Response Options"
              )}
            </button>
          </div>

          {error && (
            <div className="mt-4 p-3 bg-red-100 border-l-4 border-red-500 text-red-700">
              <p>{error}</p>
            </div>
          )}

          {/* Results Section */}
          {Object.keys(distribution).length > 0 && (
            <div className="mt-10">
              <div className="border-b border-gray-200 pb-4 mb-6">
                <h2 className="text-xl font-semibold text-gray-800">Response Type Analysis</h2>
                <p className="text-gray-600 mt-1">Based on the patient context, here are the recommended response approaches:</p>
              </div>

              <div className="grid grid-cols-1 gap-4 md:grid-cols-2">
                {Object.entries(distribution)
                  .sort((a, b) => b[1] - a[1])
                  .map(([label, prob]) => {
                    const percentage = (prob * 100).toFixed(1);
                    return (
                      <div key={label} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                        <div className="flex justify-between items-center mb-2">
                          <h3 className="font-medium text-gray-900 capitalize">{label}</h3>
                          <span className="font-bold" style={{ color: getColorForPercentage(parseFloat(percentage)) }}>
                            {percentage}%
                          </span>
                        </div>

                        <div className="w-full bg-gray-200 rounded-full h-2.5 mb-4">
                          <div 
                            className="h-2.5 rounded-full" 
                            style={{ 
                              width: `${percentage}%`,
                              backgroundColor: getColorForPercentage(parseFloat(percentage))
                            }}
                          ></div>
                        </div>

                        <button
                          className={`w-full mt-2 py-2 px-4 rounded-lg text-white font-medium ${generating && selectedType === label ? 'bg-gray-400 cursor-not-allowed' : 'bg-indigo-600 hover:bg-indigo-700'}`}
                          onClick={() => handleGenerate(label)}
                          disabled={generating}
                        >
                          {generating && selectedType === label ? "Generating..." : `Generate ${label} response`}
                        </button>
                      </div>
                    );
                  })}
              </div>
            </div>
          )}

          {/* Generated Response Section */}
          {suggestedResponse && (
            <div className="mt-10 bg-gray-50 p-6 rounded-xl border border-gray-200">
              <h2 className="text-xl font-semibold text-gray-800 mb-4">
                Suggested <span className="capitalize text-indigo-600">{selectedType}</span> Response
              </h2>
              <div className="bg-white p-5 rounded-lg border-l-4 border-indigo-500 shadow-sm">
                <p className="text-gray-700 whitespace-pre-line">{suggestedResponse}</p>
              </div>
              <div className="mt-4 flex justify-end">
                <button 
                  className="text-sm text-gray-500 hover:text-indigo-600 mr-4"
                  onClick={() => {navigator.clipboard.writeText(suggestedResponse)}}
                >
                  Copy to clipboard
                </button>
                <button 
                  className="text-sm text-gray-500 hover:text-indigo-600"
                  onClick={() => setSuggestedResponse('')}
                >
                  Dismiss
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      <footer className="mt-10 text-center text-gray-500 text-sm">
        <p>Counselor Support Assistant • AI-powered guidance for mental health professionals</p>
      </footer>
    </div>
  );
}

export default App;
