import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [context, setContext] = useState('');
  const [responseType, setResponseType] = useState(null);
  const [distribution, setDistribution] = useState({});
  const [loading, setLoading] = useState(false);
  const [suggestedResponse, setSuggestedResponse] = useState('');
  const [generating, setGenerating] = useState(false);

  const handleSubmit = async () => {
    setLoading(true);
    setSuggestedResponse('');
    try {
      const response = await axios.post('http://localhost:8000/predict', { context });
      setResponseType(response.data.response_type);
      setDistribution(response.data.distribution);
    } catch (error) {
      console.error("Prediction failed", error);
      setResponseType("Error occurred.");
    } finally {
      setLoading(false);
    }
  };

  const handleGenerate = async (type) => {
    setGenerating(true);
    try {
      const res = await axios.post('http://localhost:8000/generate', {
        context,
        response_type: type
      });
      setSuggestedResponse(res.data.generated_response);
    } catch (err) {
      console.error("Generation failed", err);
      setSuggestedResponse("Failed to generate response.");
    } finally {
      setGenerating(false);
    }
  };

  return (
    <div style={{ maxWidth: '700px', margin: '40px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h1>ðŸ§  Counselor Support Assistant</h1>
      <textarea
        rows="6"
        value={context}
        onChange={(e) => setContext(e.target.value)}
        placeholder="Enter the patient's context here..."
        style={{ width: '100%', padding: '10px', marginTop: '10px', fontSize: '16px' }}
      />
      <button
        onClick={handleSubmit}
        disabled={loading || !context}
        style={{ marginTop: '15px', padding: '10px 20px', fontSize: '16px' }}
      >
        {loading ? "Analyzing..." : "Get Response Suggestions"}
      </button>

      {Object.keys(distribution).length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3>ðŸ§ª Response Type Probabilities</h3>
          <ul>
            {Object.entries(distribution).map(([label, prob]) => (
              <li key={label} style={{ marginBottom: '5px' }}>
                <strong>{label}:</strong> {(prob * 100).toFixed(1)}%
                <button
                  style={{ marginLeft: '10px', padding: '5px 10px' }}
                  onClick={() => handleGenerate(label)}
                  disabled={generating}
                >
                  {generating ? "Generating..." : `Generate ${label}`}
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {suggestedResponse && (
        <div style={{ marginTop: '30px' }}>
          <h3>ðŸ’¬ Suggested Response</h3>
          <div style={{ backgroundColor: '#f4f4f4', padding: '15px', borderRadius: '6px', fontStyle: 'italic' }}>
            {suggestedResponse}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
